import{_ as s,c as i,o as t,ae as a}from"./chunks/framework.tuuL-SzM.js";const o="/assets/Devices-ServerView.BznmrcyL.jpg",r="/assets/Devices-RemoteView.BRnWTGNW.jpg",n="/assets/Devices-ServerView2.BxsnnC-y.jpg",l="/assets/Devices-ServerLinkPin.COmPAq2Q.jpg",c="/assets/Devices-RemoteServerPin.BKfPjHXT.jpg",d="/assets/Devices-DeviceLinked.DsBiDchH.jpg",p="/assets/Devices-ServerRemoteAuthorized.B3OIYMI2.jpg",f=JSON.parse('{"title":"Linking devices","description":"","frontmatter":{},"headers":[],"relativePath":"devices/linking.md","filePath":"devices/linking.md"}'),m={name:"devices/linking.md"};function u(h,e,v,g,b,k){return t(),i("div",null,e[0]||(e[0]=[a('<h1 id="linking-devices" tabindex="-1">Linking devices <a class="header-anchor" href="#linking-devices" aria-label="Permalink to &quot;Linking devices&quot;">​</a></h1><h4 id="to-use-a-device-with-macrame-the-server-and-remote-device-should-be-linked-if-a-device-is-not-linked-panels-can-not-be-viewed-or-used" tabindex="-1">To use a device with Macrame the server and remote device should be linked. If a device is not linked panels can not be viewed or used. <a class="header-anchor" href="#to-use-a-device-with-macrame-the-server-and-remote-device-should-be-linked-if-a-device-is-not-linked-panels-can-not-be-viewed-or-used" aria-label="Permalink to &quot;To use a device with Macrame the server and remote device should be linked. If a device is not linked panels can not be viewed or used.&quot;">​</a></h4><hr><p> </p><ol><li><p>Open the <strong>devices page</strong> on the server.</p><details class="details custom-block"><summary><em>Example:</em> Server view</summary><p><img src="'+o+'" alt="Server Device View"></p></details><p> </p></li><li><p>Scan the QR code or navigate, in a browser of your choosing, to the listed network address.</p><p><em>Example address: <a href="http://192.168.1.1:54321/devices" target="_blank" rel="noreferrer">http://192.168.1.1:54321/devices</a></em></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Your device will probably warn you because of the unsecured connection. If you want to keep using the application you should continue to the unsecured webpage.</p><p><em>If you don&#39;t I don&#39;t blame you. It&#39;s an application in development, and you&#39;re just being safe.</em></p><p>The connection might be secured in the future or a dedicated app will be build</p></div></li></ol><p> </p><ol start="3"><li><p>The device will automatically start an access request.</p><details class="details custom-block"><summary><em>Example:</em> Remote view</summary><p><img src="'+r+'" alt="Remote Server View"></p></details><p>If there are no devices linked the server will update once it has received the first access request.</p><p><em>If there are already devices shown, you should press the <strong>reload</strong> button once access has been requested.</em></p><details class="details custom-block"><summary><em>Example:</em> New device requested access</summary><p><img src="'+n+'" alt="Server New Device View"></p></details><p> </p></li><li><p>Press <strong>Link device</strong>. It will show you a one-time-pin in a dialog. Enter the pin on the remote device in the dialog that popped up.</p><details class="details custom-block"><summary><em>Example:</em> Pin dialog</summary><p><img src="'+l+'" alt="Server Pin Dialog"></p></details><details class="details custom-block"><summary><em>Example:</em> Pin input dialog</summary><p><img src="'+c+'" alt="Server Pin Dialog"></p></details><p> </p></li><li><p>If the pin is entered correctly the device will be authorized and linking will be succesfull!</p><details class="details custom-block"><summary><em>Example:</em> Device linked</summary><p><img src="'+d+'" alt="Server Pin Dialog"></p></details><details class="details custom-block"><summary><em>Example:</em> Authorized by server</summary><p><img src="'+p+'" alt="Server Pin Dialog"></p></details><p> </p></li></ol><h2 id="restarting-the-linking-process" tabindex="-1">Restarting the linking process <a class="header-anchor" href="#restarting-the-linking-process" aria-label="Permalink to &quot;Restarting the linking process&quot;">​</a></h2><p><em>If something went wrong in the standard linking process you can restart the process with the next steps</em></p><ol><li><p>Press the <strong>reload</strong> button on the device.</p><p><em>A new access request will be started.</em></p></li><li><p>Press the <strong>reload</strong> button on the server.</p><p>The device should show up and you can continue step 4 of the linking process.</p></li></ol><h2 id="removing-devices-manually" tabindex="-1">Removing devices manually <a class="header-anchor" href="#removing-devices-manually" aria-label="Permalink to &quot;Removing devices manually&quot;">​</a></h2><ol><li><p>Go to the directory Macrame is saved in. Open the <strong>devices</strong> folder. Remove the files that are inside this folder.</p></li><li><p><strong>Restart Macrame.</strong></p></li><li><p><strong>Reload</strong> the Macrame webpage on your LAN device.</p></li></ol><p> </p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>If the above mentioned steps didn&#39;t work something else might be broken.</p><p>Please report the bug. It&#39;s much appreciated.</p></div>',14)]))}const y=s(m,[["render",u]]);export{f as __pageData,y as default};
